markdownTextFunction.body:
var name, label, type, state, active, batteryLevel;
if (data && data.length) {
  name = data[0]['entityName'];
  label = data[0]['Label'];
  type = data[0]['Type'];
  state = data[0]['state'];
  active = data[0]['active'];
  batteryLevel = data[0]['batteryLevel'];
  if (active === 'false') {
    state = 'disconnected';
  }
} else {
  name = 'N/A';
  label = 'N/A';
  type = 'N/A';
  state = 'N/A';
  active = 'N/A';
  batteryLevel = 'N/A';
}

var displayBatteryLevel = showBatteryLevel(type);

var typeSvg = utils.getSmartRetailDeviceSvgIcon(type);
var encodedTypeSvg = encodeURIComponent(typeSvg);
var typeSvgUrl = 'data:image/svg+xml,' + encodedTypeSvg;

var header = '<div class="header flex flex-row items-stretch justify-start gap-2">' +
  '<div class="flex flex-col items-stretch justify-center"><button id="go-back" matTooltip="Go back" type="button" mat-icon-button><mat-icon>arrow_back</mat-icon></button></div>' +
  '<div class="flex flex-col items-stretch justify-center"><img style="vertical-align: middle;" class="title-icon mat-icon" src="'+ typeSvgUrl +'"></div>' +
  '<div class="flex flex-1 flex-col items-stretch justify-center">' +
  '<div class="title">' + label + '</div>' +
  '<div class="subtitle">' + name + '</div>' +
  '</div>' +
  '<div class="flex flex-col items-stretch justify-start"><button id="settings" data-device-type=' + encodeURIComponent(type) + ' type="button" mat-button color="primary"><mat-icon class="tb-mat-18">settings</mat-icon><span>Settings</span></button></div>' +
  '</div>';

return '<div class="main-layout flex size-full flex-col">' +
  header +
  '<div class="params flex flex-row">' +
  '<div class="param-cell flex flex-1 flex-col gap-2">' +
  '<div class="param-title">Type: </div>' +
  '<div class="param">'+ type +'</div>' +
  '</div>' +
  '<div class="param-cell flex flex-1 flex-col gap-2">' +
  '<div class="param-title">Label: </div>' +
  '<div class="param">'+ label +'</div>' +
  '</div>' +
  '<div class="param-cell flex flex-1 flex-col gap-2">' +
  '<div class="param-title">State: </div>' +
  '<div class="param">'+ getStateHtml(state) +'</div>' +
  '</div>' +
  (displayBatteryLevel ? ('<div class="param-cell flex flex-1 flex-col gap-2">' +
    '<div class="param-title">Battery level: </div>' +
    '<div class="param">'+ getBatteryLevelHtml(batteryLevel) +'</div>' +
    '</div>') : '') +
  '</div>' +
  '<div>';

function getStateHtml(state) {
  var stateSvg = getStateSvg(state);
  var encodedStateSvg = encodeURIComponent(stateSvg);
  var stateSvgUrl = 'data:image/svg+xml,' + encodedStateSvg;
  var stateLabel = getStateLabel(state);
  return '<span>'+
    '<img style="vertical-align: middle;" class="mat-icon" src="'+ stateSvgUrl +'">' +
    '<span style="vertical-align: middle; padding-left: 8px;">'+ stateLabel +'</span>' +
    '</span>';
}

function getBatteryLevelHtml(batteryLevel) {
  var batteryLevelNumber = Number(batteryLevel);
  var batteryLevelSvg = getBatteryLevelSvg(batteryLevelNumber);
  var encodedBatteryLevelSvg = encodeURIComponent(batteryLevelSvg);
  var batteryLevelSvgUrl = 'data:image/svg+xml,' + encodedBatteryLevelSvg;
  return '<div>' +
    '<img class="mat-icon" src="'+ batteryLevelSvgUrl +'">' +
    '<span style="padding-left: 8px; padding-right: 8px;">' + batteryLevelNumber.toFixed(0) + '%</span>' +
    '<button id="battery-level" matTooltip="Battery Level History" type="button" mat-flat-button color="primary"><mat-icon>bar_chart</mat-icon></button>' +
    '</div>';
}

function getStateSvg(state) {
  switch (state) {
    case 'critical':
      return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
        '<circle cx="10" cy="10" r="6" fill="#E64A19"/>' +
        '</svg>';
    case 'major':
      return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
        '<circle cx="10" cy="10" r="6" fill="#D5C21B"/>' +
        '</svg>';
    case 'normal':
      return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
        '<circle cx="10" cy="10" r="6" fill="#1F8B4D"/>' +
        '</svg>';
    case 'disconnected':
      return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
        '<g clip-path="url(#clip0_2839_52564)">' +
        '<circle cx="10" cy="10" r="6" fill="#CAC1D2"/>' +
        '<path d="M1.92896 16.728L16.0711 2.58589L17.4853 4.00011L3.34317 18.1422L1.92896 16.728Z" fill="#CAC1D2"/>' +
        '<path d="M2.10692 16.3171C1.81393 16.0241 1.81393 15.5491 2.10692 15.2561L15.1852 2.17788C15.4781 1.88489 15.9532 1.88489 16.2462 2.17788C16.5392 2.47088 16.5392 2.94591 16.2462 3.2389L3.16794 16.3171C2.87495 16.6101 2.39991 16.6101 2.10692 16.3171Z" fill="white"/>' +
        '</g>' +
        '<defs>' +
        '<clipPath id="clip0_2839_52564">' +
        '<rect width="20" height="20" fill="white"/>' +
        '</clipPath>' +
        '</defs>' +
        '</svg>';
    default:
      return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
        '<circle cx="10" cy="10" r="6" fill="#AAAAAA"/>' +
        '</svg>';
  }
}

function getBatteryLevelSvg(batteryLevel) {
