  } else if (batteryLevel <= 35) {
    battryLevelSvg = '<path d="M14 14H6V16H14V14Z" fill="#E64A19"/>' +
      '<path fill-rule="evenodd" clip-rule="evenodd" d="M5 3H7V2C7 1.44772 7.44772 1 8 1H12C12.5523 1 13 1.44772 13 2V3H15C15.5523 3 16 3.44772 16 4V17C16 17.5523 15.5523 18 15 18H5C4.44772 18 4 17.5523 4 17V4C4 3.44772 4.44772 3 5 3ZM5 4H8V2H12V4H15V17H5V4Z" fill="#E64A19"/>';
  } else if (batteryLevel <= 65) {
    battryLevelSvg = '<path d="M6 14H14V16H6V14Z" fill="#D5C21B"/>' +
      '<path d="M14 11H6V13H14V11Z" fill="#D5C21B"/>' +
      '<path fill-rule="evenodd" clip-rule="evenodd" d="M7 3H5C4.44772 3 4 3.44772 4 4V17C4 17.5523 4.44772 18 5 18H15C15.5523 18 16 17.5523 16 17V4C16 3.44772 15.5523 3 15 3H13V2C13 1.44772 12.5523 1 12 1H8C7.44772 1 7 1.44772 7 2V3ZM8 4H5V17H15V4H12V2H8V4Z" fill="#D5C21B"/>';
  } else if (batteryLevel <= 95) {
    battryLevelSvg = '<path d="M14 14H6V16H14V14Z" fill="#1F8B4D"/>' +
      '<path d="M6 11H14V13H6V11Z" fill="#1F8B4D"/>' +
      '<path d="M14 8H6V10H14V8Z" fill="#1F8B4D"/>' +
      '<path fill-rule="evenodd" clip-rule="evenodd" d="M5 3H7V2C7 1.44772 7.44772 1 8 1H12C12.5523 1 13 1.44772 13 2V3H15C15.5523 3 16 3.44772 16 4V17C16 17.5523 15.5523 18 15 18H5C4.44772 18 4 17.5523 4 17V4C4 3.44772 4.44772 3 5 3ZM5 4H8V2H12V4H15V17H5V4Z" fill="#1F8B4D"/>';
  } else {
    battryLevelSvg = '<path d="M6 14H14V16H6V14Z" fill="#1F8B4D"/>' +
      '<path d="M14 11H6V13H14V11Z" fill="#1F8B4D"/>' +
      '<path d="M6 8H14V10H6V8Z" fill="#1F8B4D"/>' +
      '<path d="M14 5H6V7H14V5Z" fill="#1F8B4D"/>' +
      '<path fill-rule="evenodd" clip-rule="evenodd" d="M7 3H5C4.44772 3 4 3.44772 4 4V17C4 17.5523 4.44772 18 5 18H15C15.5523 18 16 17.5523 16 17V4C16 3.44772 15.5523 3 15 3H13V2C13 1.44772 12.5523 1 12 1H8C7.44772 1 7 1.44772 7 2V3ZM8 4H5V17H15V4H12V2H8V4Z" fill="#1F8B4D"/>';
  }
  return '<svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg">' +
    battryLevelSvg +
    '</svg>';
}

function getStateLabel(state) {
