const targetElement = $($event.target).closest('#settings', widgetContext.$container[0]);

var type = decodeURIComponent($(targetElement).attr('data-device-type'));

var stateId = getSettingsState(type);

var params = {
    selectedDevice: {
        entityId: entityId,
        entityName: entityName,
        entityLabel: entityLabel,
    },
    targetEntityParamName: 'selectedDevice'
};

widgetContext.actionsApi.openDashboardStateInPopover($event, stateId, params, true, 'bottomRight', true, '400px', getSettingsHeight(type));

function getSettingsState(type) {
    switch (type) {
        case 'Smart Shelf':
            return 'smart_shelf_settings';
        case 'Freezer':            
            return 'freezer_settings';
        case 'Chiller':            
            return 'chiller_settings';                      
        case 'Smart Bin':
            return 'smart_bin_settings';          
        case 'Door Sensor':
            return 'door_settings';
