let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let http = $injector.get(widgetContext.servicesMap.get('http'));

openAddAssetDialog();

function openAddAssetDialog() {
    // Uses your existing htmlTemplate; trigger unchanged
    customDialog.customDialog(htmlTemplate, AddAssetDialogController).subscribe();
}

function AddAssetDialogController(instance) {
    let vm = instance;
    
    vm.machineDevices = [];
    
    let machinesGroupId = '45504450-ae8f-11f0-a60f-a7f9e67d958a';
    let jobCardsGroupId = '45493f70-ae8f-11f0-a60f-a7f9e67d958a'; // <-- add to Job Cards group
    let pageSize = 1000;
    let page = 0;
    let url = '/api/entityGroup/' + machinesGroupId + '/devices?pageSize=' + pageSize + '&page=' + page;
    
    http.get(url).subscribe(
        function(response) {
            if (response && response.data) {
                vm.machineDevices = response.data;
                console.log('Loaded ' + vm.machineDevices.length + ' devices from Machines group');
            }
        },
        function(error) {
            console.error('Error loading devices from group:', error);
        }
    );
    
    vm.addAssetFormGroup = vm.fb.group({
      assetName: ['', [vm.validators.required]],
      assetLabel: [''],
      deviceRef: [null, [vm.validators.required]],
      attributes: vm.fb.group({
          // NOTE: machine_number removed
          shift_name: ['', [vm.validators.required]],
          part_name: ['', [vm.validators.required]],
          plant_name: ['', [vm.validators.required]],
          line_location: ['', [vm.validators.required]],
          cfg_target_qty_pcs: ['', [vm.validators.required]],
          cfg_start_ts: [null, [vm.validators.required]],
          cfg_shift_hours: ['', [vm.validators.required]],
          cfg_min_benchmark: ['', [vm.validators.required]]
      })
    });
    
    vm.cancel = function() {
        vm.dialogRef.close(null);
    };
    
    vm.save = function() {
        vm.addAssetFormGroup.markAsPristine();
        let asset = {
            name: vm.addAssetFormGroup.get('assetName').value,
            type: 'Job Card',
            label: vm.addAssetFormGroup.get('assetLabel').value
        };
        assetService.saveAsset(asset, jobCardsGroupId).subscribe( // <-- pass group id here
            function (savedAsset) {
                saveAttributes(savedAsset.id).subscribe(
                    function () {
                        saveDeviceRelation(savedAsset.id).subscribe(
                            function () {
                                widgetContext.updateAliases();
                                vm.dialogRef.close(null);
                            },
                            function (error) {
                                console.error('Error saving device relation:', error);
                                widgetContext.updateAliases();
                                vm.dialogRef.close(null);
                            }
                        );
                    }
                );
            }
        );
    };
    
    function saveAttributes(entityId) {
        let attributes = vm.addAssetFormGroup.get('attributes').value;
        let deviceRef = vm.addAssetFormGroup.get('deviceRef').value;
        
        // NOTE: machine_number removed from payload
        let attributesArray = [
            {key: 'shift_name', value: String(attributes.shift_name || '')},
            {key: 'part_name', value: String(attributes.part_name || '')},
            {key: 'plant_name', value: String(attributes.plant_name || '')},
            {key: 'line_location', value: String(attributes.line_location || '')},
            {key: 'cfg_target_qty_pcs', value: parseInt(attributes.cfg_target_qty_pcs, 10)},
            {key: 'cfg_shift_hours', value: parseFloat(attributes.cfg_shift_hours)},
            {key: 'cfg_min_benchmark', value: parseFloat(attributes.cfg_min_benchmark)},
            {key: 'job_status', value: 'planned'}
        ];
        
        let startTs = attributes.cfg_start_ts;
        if (startTs) {
            let ms = (startTs instanceof Date) ? startTs.getTime()
                   : (typeof startTs === 'number') ? startTs
                   : new Date(String(startTs)).getTime();
            if (!isNaN(ms)) {
                attributesArray.push({key: 'cfg_start_ts', value: ms});
            }
        }
        
        if (deviceRef) {
            let devId = (deviceRef.id && deviceRef.id.id) ? deviceRef.id.id
                      : (typeof deviceRef.id === 'string') ? deviceRef.id : null;
            let devName = deviceRef.name || deviceRef.entityName || null;
            if (devId) attributesArray.push({key: 'machine_device_id', value: devId});
            if (devName) attributesArray.push({key: 'machine_device_name', value: devName});
        }
        
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
    
    function saveDeviceRelation(assetEntityId) {
        let deviceRef = vm.addAssetFormGroup.get('deviceRef').value;
        if (!deviceRef || !deviceRef.id) {
            return widgetContext.rxjs.of(null);
        }
        
        let relation = {
            from: { 
                entityType: 'DEVICE', 
                id: deviceRef.id.id || deviceRef.id 
            },
            to: { 
                entityType: 'ASSET', 
                id: assetEntityId.id || assetEntityId 
            },
            type: 'ACTIVE_JOB',
            typeGroup: 'COMMON'
        };
        
        return entityRelationService.saveRelation(relation);
    }
}

