let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let http = $injector.get(widgetContext.servicesMap.get('http'));

openEditAssetDialog();

function openEditAssetDialog() {
    customDialog.customDialog(htmlTemplate, EditAssetDialogController).subscribe();
}

function EditAssetDialogController(instance) {
    let vm = instance;

    vm.asset = null;
    vm.attributes = {};
    vm.machineDevices = [];

    // Load devices from Machines group (same as Add dialog)
    let machinesGroupId = '0655e4c0-a440-11f0-83ea-d7568d658832';
    let pageSize = 1000;
    let page = 0;
    let url = '/api/entityGroup/' + machinesGroupId + '/devices?pageSize=' + pageSize + '&page=' + page;

    http.get(url).subscribe(
        function(response) {
            if (response && response.data) {
                vm.machineDevices = response.data;
            }
            initFormAndData(); // proceed after devices load
        },
        function(error) {
            console.error('Error loading devices from group:', error);
            initFormAndData(); // proceed anyway
        }
    );

    function initFormAndData() {
        vm.editAssetFormGroup = vm.fb.group({
          assetName: ['', [vm.validators.required]],
          assetLabel: [''],
          deviceRef: [null],
          attributes: vm.fb.group({
              shift_name: ['', [vm.validators.required]],
              part_name: ['', [vm.validators.required]],
              plant_name: ['', [vm.validators.required]],
              machine_number: ['', [vm.validators.required]],
              line_location: ['', [vm.validators.required]],
              cfg_target_qty_pcs: ['', [vm.validators.required]],
              cfg_start_ts: [null, [vm.validators.required]],
              cfg_shift_hours: ['', [vm.validators.required]],
              cfg_min_benchmark: ['', [vm.validators.required]]
          })
        });

        getEntityInfo();
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editAssetFormGroup.markAsPristine();

        // Update and save asset entity
        vm.asset.name = vm.editAssetFormGroup.get('assetName').value;
        vm.asset.label = vm.editAssetFormGroup.get('assetLabel').value;
        vm.asset.type = vm.asset.type || 'Job Card';

        assetService.saveAsset(vm.asset).subscribe(
            function () {
                // Save attributes after entity save
                saveAttributes().subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };

    function getEntityInfo() {
        assetService.getAsset(entityId.id).subscribe(
            function (asset) {
                // Load specific Job Card attributes used in Add dialog
                let keys = [
                  'shift_name','part_name','plant_name','machine_number','line_location',
                  'cfg_target_qty_pcs','cfg_start_ts','cfg_shift_hours','cfg_min_benchmark',
                  'machine_device_id','machine_device_name','job_status'
                ];

                attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', keys).subscribe(
                   function (attributes) {
                        for (let i = 0; i < attributes.length; i++) {
                            vm.attributes[attributes[i].key] = attributes[i].value;
                        }
                        vm.asset = asset;

                        // Resolve deviceRef from machine_device_id if available
                        let deviceRef = null;
                        if (vm.attributes.machine_device_id && Array.isArray(vm.machineDevices)) {
                            let idToFind = String(vm.attributes.machine_device_id);
                            for (let d of vm.machineDevices) {
                                let did = (d.id && d.id.id) ? String(d.id.id) : (typeof d.id === 'string' ? d.id : '');
                                if (did === idToFind) { deviceRef = d; break; }
                            }
                        }

                        // Convert start ts to Date for the picker if numeric
                        let startVal = vm.attributes.cfg_start_ts;
                        let startDate = null;
                        if (startVal !== undefined && startVal !== null) {
                            let ms = (startVal instanceof Date) ? startVal.getTime()
                                   : (typeof startVal === 'number') ? startVal
                                   : new Date(String(startVal)).getTime();
                            if (!isNaN(ms)) startDate = new Date(ms);
                        }

                        vm.editAssetFormGroup.patchValue(
                            {
                                assetName: vm.asset.name,
                                assetLabel: vm.asset.label,
                                deviceRef: deviceRef,
                                attributes: {
                                    shift_name: vm.attributes.shift_name || '',
                                    part_name: vm.attributes.part_name || '',
                                    plant_name: vm.attributes.plant_name || '',
                                    machine_number: vm.attributes.machine_number || '',
                                    line_location: vm.attributes.line_location || '',
                                    cfg_target_qty_pcs: vm.attributes.cfg_target_qty_pcs,
                                    cfg_start_ts: startDate,
                                    cfg_shift_hours: vm.attributes.cfg_shift_hours,
                                    cfg_min_benchmark: vm.attributes.cfg_min_benchmark
                                }
                            }, {emitEvent: false}
                        );
                   }
                );
            }
        );
    }

    function saveAttributes() {
        let attributes = vm.editAssetFormGroup.get('attributes').value;
        let deviceRef = vm.editAssetFormGroup.get('deviceRef').value;

        let attributesArray = [
            {key: 'shift_name', value: String(attributes.shift_name || '')},
            {key: 'part_name', value: String(attributes.part_name || '')},
            {key: 'plant_name', value: String(attributes.plant_name || '')},
            {key: 'machine_number', value: String(attributes.machine_number || '')},
            {key: 'line_location', value: String(attributes.line_location || '')},
            {key: 'cfg_target_qty_pcs', value: parseInt(attributes.cfg_target_qty_pcs, 10)},
            {key: 'cfg_shift_hours', value: parseFloat(attributes.cfg_shift_hours)},
            {key: 'cfg_min_benchmark', value: parseFloat(attributes.cfg_min_benchmark)}
        ];

        // Handle start timestamp
        let startTs = attributes.cfg_start_ts;
        if (startTs) {
            let ms = (startTs instanceof Date) ? startTs.getTime()
                   : (typeof startTs === 'number') ? startTs
                   : new Date(String(startTs)).getTime();
            if (!isNaN(ms)) {
                attributesArray.push({key: 'cfg_start_ts', value: ms});
            }
        } else {
            // allow clearing it explicitly
            attributesArray.push({key: 'cfg_start_ts', value: null});
        }

        // Preserve or update job_status if present
        if (vm.attributes && vm.attributes.job_status !== undefined) {
            attributesArray.push({key: 'job_status', value: vm.attributes.job_status});
        }

        // Update machine device attributes from selection
        if (deviceRef) {
            let devId = (deviceRef.id && deviceRef.id.id) ? deviceRef.id.id
                      : (typeof deviceRef.id === 'string') ? deviceRef.id : null;
            let devName = deviceRef.name || deviceRef.entityName || null;
            if (devId) attributesArray.push({key: 'machine_device_id', value: devId});
            if (devName) attributesArray.push({key: 'machine_device_name', value: devName});
        } else {
            // If user cleared device selection, null out attributes
            attributesArray.push({key: 'machine_device_id', value: null});
            attributesArray.push({key: 'machine_device_name', value: null});
        }

        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, 'SERVER_SCOPE', attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));

openDeleteAssetDialog();

function openDeleteAssetDialog() {
    let title = "Are you sure you want to delete the asset " + entityName +  "?";
    let content = "Be careful, after the confirmation, the asset and all related data will become unrecoverable!";
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function (result) {
            if (result) {
                deleteAsset();
            }
        }
    );
}

function deleteAsset() {
    assetService.deleteAsset(entityId.id).subscribe(
        function () {
            widgetContext.updateAliases();
        }
    );
}

