/* ============================================================
 * Centered overlay helper (singleton per widget)
 * Same style as your guide, but we can hide the button.
 * ============================================================ */
function createCenterOverlay(ctx, options) {
  options = options || {};
  var buttonLabel   = options.buttonLabel  || 'Open Job Card';
  var targetStateId = options.targetStateId || 'job_card';

  var overlay = null;

  function ensureOverlay() {
    if (overlay) return overlay;

    var root =
      ctx.$container && ctx.$container[0]
        ? ctx.$container[0]
        : document.body;

    // Ensure host can contain absolute children
    var computedPos = window.getComputedStyle(root).position;
    if (computedPos === 'static' || !computedPos) {
      root.style.position = 'relative';
    }

    var ov = document.createElement('div');
    ov.style.position       = 'absolute';
    ov.style.left           = '0';
    ov.style.top            = '0';
    ov.style.width          = '100%';
    ov.style.height         = '100%';
    ov.style.display        = 'none';
    ov.style.alignItems     = 'center';
    ov.style.justifyContent = 'center';
    ov.style.zIndex         = '9999';
    ov.style.pointerEvents  = 'none';
    ov.style.background     = 'rgba(0, 0, 0, 0.25)';

    var box = document.createElement('div');
    box.style.minWidth      = '260px';
    box.style.maxWidth      = '420px';
    box.style.padding       = '18px 28px';
    box.style.borderRadius  = '12px';
    box.style.background    = 'rgba(8,22,43,0.98)';
    box.style.color         = '#e5edf7';
    box.style.boxShadow     = '0 12px 28px rgba(0,0,0,0.6)';
    box.style.pointerEvents = 'auto';

    var msg = document.createElement('div');
    msg.style.fontSize     = '18px';
    msg.style.lineHeight   = '1.4';
    msg.style.fontWeight   = '700';
    msg.style.textAlign    = 'center';
    msg.style.marginBottom = '14px';
    box.appendChild(msg);

    var btn = document.createElement('button');
    btn.textContent        = buttonLabel;
    btn.style.marginTop    = '4px';
    btn.style.padding      = '8px 16px';
    btn.style.borderRadius = '999px';
    btn.style.border       = '1px solid #e5edf7';
    btn.style.background   = '#e5edf7';
    btn.style.color        = '#0f243e';
    btn.style.fontSize     = '13px';
    btn.style.fontWeight   = '600';
    btn.style.cursor       = 'pointer';
    btn.style.outline      = 'none';
    btn.style.boxShadow    = '0 2px 6px rgba(0,0,0,0.45)';
    btn.style.display      = 'inline-block';

    btn.addEventListener('click', function (e) {
      e.stopPropagation();
      var sc = ctx.stateController ||
               (ctx.dashboard && ctx.dashboard.stateController);
      if (!sc) {
        ov.style.display = 'none';
        return;
      }

      var params = (ov._stateParams && typeof ov._stateParams === 'object')
        ? ov._stateParams
        : {};

      if (typeof sc.updateState === 'function') {
        sc.updateState(targetStateId || null, params, false);
      } else if (typeof sc.openState === 'function') {
        sc.openState(targetStateId || null, params, false);
      }

      ov.style.display = 'none';
    });

    box.appendChild(btn);
    ov.appendChild(box);
    root.appendChild(ov);

    ov._msg         = msg;
    ov._btn         = btn;
    ov._hideTimeout = null;
    ov._stateParams = null;

    overlay = ov;
    return overlay;
  }

  function show(text, timeoutMs, showButton, stateParams) {
    var ov = ensureOverlay();

    if (ov._hideTimeout) {
      clearTimeout(ov._hideTimeout);
      ov._hideTimeout = null;
    }

    ov._msg.textContent = text || '';

    if (showButton === false) {
      ov._btn.style.display = 'none';
      ov._stateParams = null;
    } else {
      ov._btn.style.display = 'inline-block';
      ov._stateParams = stateParams || null;
    }

    ov.style.display = 'flex';

    var delay = typeof timeoutMs === 'number' ? timeoutMs : 2000;
    if (delay > 0) {
      ov._hideTimeout = setTimeout(function () {
        ov.style.display = 'none';
        ov._hideTimeout = null;
      }, delay);
    }
  }

  return {
    show: show
  };
}

/* ============================================================
 * Ribbon widget
 * ============================================================ */
self.onInit = function () {
  var ctx = self.ctx;

  // Overlay instance for this widget
  var centerOverlay = createCenterOverlay(ctx, {
    buttonLabel: 'Go to Job Card Panel',
    targetStateId: 'job_card'
  });

  // ðŸ”¹ Global scalers
  var NAV_UI_SCALE   = 1.3;  // quick-link pills & text
  var ARROW_UI_SCALE = 1.3;  // arrow button & triangle size

  function navPx(v)   { return (v * NAV_UI_SCALE) + 'px'; }
  function arrowPx(v) { return (v * ARROW_UI_SCALE) + 'px'; }

  var navStates = [
    { id: "plant_view",          label: "Plant View" },
    { id: "line_view",          label: "Line View" },
    { id: "machine_dashboard",  label: "Machine Dashboard" },
    { id: "visualize_metrics",  label: "Visualize Metrics" },
    { id: "duration_header",    label: "Duration Header" },
    { id: "occurrence_header",  label: "Occurrence Header" },
    { id: "job_card",           label: "Job Card Panel" },
    { id: "control_panel",      label: "Control Panel" },
    { id: "ai_analysis",        label: "AI Analysis" }
  ];

  var quickLinkButtons = {};

  function getStateCtrl() {
    return ctx.stateController ||
           (ctx.dashboard && ctx.dashboard.stateController) ||
           null;
  }

  function getCurrentStateId(stateCtrl) {
    stateCtrl = stateCtrl || getStateCtrl();
    if (!stateCtrl || typeof stateCtrl.getStateId !== 'function') return null;
    try {
      return stateCtrl.getStateId();
    } catch (e) {
      console.error('[StateNav] getStateId failed:', e);
      return null;
    }
  }

  function findIndexById(stateId) {
    if (!stateId) return -1;
    for (var i = 0; i < navStates.length; i++) {
      if (navStates[i].id === stateId) return i;
    }
    return -1;
  }

  function setBtnActive(btn, active) {
    if (!btn) return;
    if (active) {
      btn.style.background  = '#e5edf7';
      btn.style.color       = '#0f243e';
      btn.style.borderColor = '#e5edf7';
    } else {
      btn.style.background  = 'rgba(8,22,43,0.95)';
      btn.style.color       = '#e5edf7';
      btn.style.borderColor = 'rgba(229,237,247,0.6)';
    }
  }

  function highlightQuickLink(activeId) {
    Object.keys(quickLinkButtons).forEach(function (id) {
      setBtnActive(quickLinkButtons[id], id === activeId);
    });
  }

  // ðŸ”¹ Read jobcard + param key from state params
  function getJobcardFromStateParams() {
    var stateCtrl = getStateCtrl();
    if (!stateCtrl || typeof stateCtrl.getStateParams !== 'function') {
      return null;
    }

    var params;
    try {
      params = stateCtrl.getStateParams();
    } catch (e) {
      console.error('[StateNav] getStateParams failed:', e);
      return null;
    }

    console.log('[StateNav] current state params:', params);
    if (!params) return null;

    var key = null;
    if (params.targetEntityParamName && params[params.targetEntityParamName]) {
      key = params.targetEntityParamName;
    } else if (params.jobcard) {
      key = 'jobcard';
    } else {
      return null;
    }

    var job = params[key];
    if (!job) return null;

    return { key: key, job: job };
  }

  // ðŸ”¹ Update the current state (no stacking) + forward jobcard + optional machineId
  function goToState(targetId) {
    var stateCtrl = getStateCtrl();
    if (!stateCtrl) {
      console.warn('[StateNav] No state controller available');
      return;
    }

    var currentId = getCurrentStateId(stateCtrl);
    var jc        = getJobcardFromStateParams();

    // âœ… Guard 1: leaving job_card WITHOUT jobcard param,
    // and trying to go anywhere except plant_sld
    if (currentId === 'job_card' &&
        targetId !== 'plant_sld' &&
        (!jc || !jc.job)) {

      console.warn('[StateNav] Leaving job_card without jobcard; showing overlay.');
      if (centerOverlay && typeof centerOverlay.show === 'function') {
        centerOverlay.show('Please click on a job card in table', 2000, false);
      }
      return;
    }

    // âœ… Guard 2: trying to navigate *to* job_card via ribbon with no jobcard
    if (targetId === 'job_card' &&
        (!jc || !jc.job)) {

      console.warn('[StateNav] job_card requested without jobcard; showing overlay.');
      if (centerOverlay && typeof centerOverlay.show === 'function') {
        centerOverlay.show('Please click on a job card in table', 2000, false);
      }
      return;
    }

    var paramsToPass = {};

    if (jc && jc.job) {
      // forward jobcard param as-is
      paramsToPass.targetEntityParamName = jc.key;
      paramsToPass[jc.key] = jc.job;

      // jobcard.entityLabel = deviceId (from SLD widget)
      // jobcard.entityName  = machine name
      var deviceId   = jc.job.entityLabel || null;
      var deviceName = jc.job.entityName || jc.job.entityLabel || '';

      // For header states, add machineId param as full entity descriptor
      if ((targetId === 'duration_header' || targetId === 'occurrence_header') && deviceId) {
        paramsToPass.machineId = {
          entityId:    { entityType: 'DEVICE', id: deviceId },
          entityName:  deviceName,
          entityLabel: deviceName
        };
        console.log('[StateNav] machineId param set to entity:', paramsToPass.machineId);
      } else if (targetId === 'duration_header' || targetId === 'occurrence_header') {
        console.warn('[StateNav] No deviceId on jobcard; machineId entity not set');
      }
    } else {
      if (targetId === 'duration_header' || targetId === 'occurrence_header') {
        console.warn('[StateNav] No jobcard param present; cannot set machineId');
      }
    }

    var nextStateId = targetId || null;
    console.log('[StateNav] Updating state to:', nextStateId, 'with params:', paramsToPass);

    if (typeof stateCtrl.updateState === 'function') {
      stateCtrl.updateState(nextStateId, paramsToPass, false);
    } else {
      console.warn('[StateNav] stateCtrl.updateState is not available; fallback to setState if present.');
      if (typeof stateCtrl.setState === 'function') {
        stateCtrl.setState(nextStateId, paramsToPass, false);
      }
    }

    highlightQuickLink(targetId);
  }

  function navigateRelative(offset) {
    var stateCtrl = getStateCtrl();
    if (!stateCtrl) return;

    var currentId  = getCurrentStateId(stateCtrl);
    var currentIdx = findIndexById(currentId);
    if (currentIdx < 0) return;

    var newIdx = currentIdx + offset;
    if (newIdx < 0) newIdx = navStates.length - 1;
    else if (newIdx >= navStates.length) newIdx = 0;

    goToState(navStates[newIdx].id);
  }

  function renderQuickLinks() {
    var root      = ctx.$container && ctx.$container[0] ? ctx.$container[0] : document;
    var container = root.querySelector('#quick-links');
    if (!container) {
      console.warn('[StateNav] #quick-links not found');
      return;
    }

    container.innerHTML = '';
    quickLinkButtons = {};

    navStates.forEach(function (st) {
      var btn = document.createElement('div');
      btn.textContent = st.label;
      btn.setAttribute('data-state-id', st.id);

      // ðŸ”¹ NAV scale applied here
      btn.style.display      = 'inline-block';
      btn.style.padding      = navPx(4) + ' ' + navPx(14);
      btn.style.margin       = '0 4px';
      btn.style.borderRadius = '999px';
      btn.style.border       = '1px solid rgba(229,237,247,0.6)';
      btn.style.background   = 'rgba(8,22,43,0.95)';
      btn.style.color        = '#e5edf7';
      btn.style.fontSize     = navPx(13);
      btn.style.fontWeight   = '600';
      btn.style.whiteSpace   = 'nowrap';
      btn.style.cursor       = 'pointer';
      btn.style.boxShadow    = '0 0 0 1px rgba(0,0,0,0.35)';
      btn.style.userSelect   = 'none';

      btn.addEventListener('click', function () {
        console.log('[StateNav] Quick link:', st.id);
        goToState(st.id);
      });

      container.appendChild(btn);
      quickLinkButtons[st.id] = btn;
    });
  }

  function styleArrowButton(btnId, isLeft) {
    var root = ctx.$container && ctx.$container[0] ? ctx.$container[0] : document;
    var btn  = root.querySelector('#' + btnId);
    if (!btn) return;

    btn.style.width        = arrowPx(36);
    btn.style.height       = arrowPx(36);
    btn.style.borderRadius = '999px';

    var arrow = btn.firstElementChild;
    if (!arrow) return;

    arrow.style.width  = '0';
    arrow.style.height = '0';
    arrow.style.borderTop    = arrowPx(7)  + ' solid transparent';
    arrow.style.borderBottom = arrowPx(7)  + ' solid transparent';

    if (isLeft) {
      arrow.style.borderRight = arrowPx(10) + ' solid #e5edf7';
      arrow.style.borderLeft  = '0';
    } else {
      arrow.style.borderLeft  = arrowPx(10) + ' solid #e5edf7';
      arrow.style.borderRight = '0';
    }
  }

  function bindArrows() {
    var root    = ctx.$container && ctx.$container[0] ? ctx.$container[0] : document;
    var backBtn = root.querySelector('#back-btn');
    var fwdBtn  = root.querySelector('#forward-btn');

    if (backBtn) {
      backBtn.addEventListener('click', function () {
        navigateRelative(-1);
      });
    }
    if (fwdBtn) {
      fwdBtn.addEventListener('click', function () {
        navigateRelative(1);
      });
    }

    styleArrowButton('back-btn', true);
    styleArrowButton('forward-btn', false);
  }

  function syncActiveFromState() {
    var stateCtrl = getStateCtrl();
    if (!stateCtrl) return;
    var curId = getCurrentStateId(stateCtrl);
    highlightQuickLink(curId);
    // Removed the old auto-popup-on-job_card logic
  }

  setTimeout(function () {
    renderQuickLinks();
    bindArrows();
    syncActiveFromState();
  }, 0);
};

self.onDataUpdated = function () {};
self.onResize      = function () {};
self.onDestroy     = function () {};

