.attribute-update-form {
    overflow: hidden;
    height: 100%;
    display: flex;
    flex-direction: column;
}

.entity-title {
    font-weight: bold;
    font-size: 22px;
    padding-top: 12px;
    padding-bottom: 6px;
    color: #666;
}

.attribute-update-form__grid {
    display: flex;
}
.grid__element:first-child {
    flex-direction: column;
    flex: 1;
}
.grid__element.horizontal-alignment {
    flex-direction: row;
}
.grid__element:last-child {
    align-items: center;
    margin-left: 2px;
}
.grid__element {
    display: flex;
}

.attribute-update-form .mat-button.mat-icon-button {
    margin: 0;
    width: 24px;
    min-width: 24px;
    height: 24px;
    min-height: 24px;
    padding: 0 !important;
    margin: 0 !important;
    line-height: 20px;
}

