    width: 20px;
    min-width: 20px;
    height: 20px;
    min-height: 20px;
    font-size: 20px;
}

.attribute-update-form mdp-date-picker,
.attribute-update-form mdp-time-picker {
    width: 100%;
}

.attribute-update-form mdp-date-picker md-input-container,
.attribute-update-form mdp-time-picker md-input-container {
    margin: 5px 0 5px;
    width: 100%;
}

.attribute-update-form grid__element .button.mat-icon-button {
    margin: 2px 0 0;
    padding: 0;
    width: 22px;
    height: 20px;
}

.attribute-update-form mat-form-field .mat-form-field-infix {
    padding: 0;
    border-top: .6em solid transparent;
}

.attribute-update-form.small-width mdp-date-picker md-input-container,
