descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },

            "retrieveMethod": {
                "title": "Retrieve string value using method",
                "type": "string",
                "default": "rpc"
            },
            "retrieveAttributeKey": {
                "title": "Retrieve Attribute/Timeseries value key (only when subscribe for attribute/timeseries method)",
                "type": "string",
                "default": ""
            },
            "retrieveRPCMethod": {
                "title": "Retrieve value using RPC method (only when call RPC get value method)",
                "type": "string",
                "default": ""
            },
            "parseValueFunction": {
                "title": "Parse value function, f(data), returns string",
                "type": "string",
                "default": ""
            },

            "updateMethod": {
                "title": "Update string value using method",
                "type": "string",
                "default": "rpc"
            },
            "updateAttributeKey": {
                "title": "Update Attribute/Timeseries value key (only when update for attribute/timeseries method)",
                "type": "string",
                "default": ""
            },
            "updateRPCMethod": {
                "title": "Update value using RPC method (only when call RPC set value method)",
                "type": "string",
                "default": ""
            },
            "convertValueFunction": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": ""
            },

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },

            "buttonState":{
                "type": "object",
                "title": "Button state",
                "properties": {
                    "on": {
                        "type": "object",
                        "title": "On state",
                        "properties": {
                            "value": {
                                "title": "On state attribute value",
                                "type": "string",
                                "default": "true",
                                "description": "On state attribute value."
                            },
                            "label": {
                                "title": "On state label",
                                "type": "string",
                                "default": "On state Label"
                            },
                            "isRaised": {
                                "type": "boolean",
                                "title": "Raised",
                                "default": true
                            },
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": true
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "On state background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "On state text color",
                                "default": null
                            }
                        }, 
                        "required": [
                            "value",
                            "label"
                        ]
                    },
                    "off": {
                        "type": "object",
                        "title": "Off state",
                        "properties": {
                            "value": {
                                "title": "Off state attribute value",
                                "type": "string",
                                "default": "false",
                                "description": "Off state attribute value."
                            },
                            "label": {
                                "title": "Off state label",
                                "type": "string",
                                "default": "Off state label"
                            },
                            "isRaised": {
                                "type": "boolean",
                                "title": "Raised",
                                "default": true
                            },
                            "isPrimary": {
                                "type": "boolean",
                                "title": "Primary color",
                                "default": false
                            },
                            "bgColor": {
                                "type": "string",
                                "title": "Off state background color",
                                "default": null
                            },
                            "textColor": {
                                "type": "string",
                                "title": "Off state text color",
                                "default": null
                            }
                        }, 
                        "required": [
                            "value",
                            "label"
                        ]
                    }
                }, 
                "required": [
                    "on",
                    "off"
                ]
            },

            "required": [
                "retrieveMethod",
                "updateMethod",
                "requestTimeout",
                "buttonState"
            ]
        }
    },
    "form": [
        "title",

        {
            "key": "retrieveMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "none",
                    "label": "Don't retrieve"
                },
                {
                    "value": "attribute",
                    "label": "Subscribe for attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Subscribe for timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC get value method"
                }
            ]
        },
        "retrieveAttributeKey",
        "retrieveRPCMethod",
        {
            "key": "parseValueFunction",
            "type": "javascript"
        },

        {
            "key": "updateMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [
                {
                    "value": "SHARED_SCOPE",
                    "label": "Update shared attribute"
                },
                {
                    "value": "SERVER_SCOPE",
                    "label": "Update server attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Update timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC set value method"
                }
            ]
        },
        "updateAttributeKey",
        "updateRPCMethod",
        {
            "key": "convertValueFunction",
            "type": "javascript"
        },

        "requestTimeout",

        {
            "key": "buttonState",
            "items" :[
                {
                    "key": "buttonState.on",
                    "items": [
                        "buttonState.on.value",
                        "buttonState.on.label",
                        "buttonState.on.isRaised",
                        "buttonState.on.isPrimary",
                        {
                            "key": "buttonState.on.bgColor",
                            "type": "color"
                        }, {
                            "key": "buttonState.on.textColor",
                            "type": "color"
                        }
                    ]
                }, {
                    "key": "buttonState.off",
                    "items": [
                        "buttonState.off.value",
                        "buttonState.off.label",
                        "buttonState.off.isRaised",
                        "buttonState.off.isPrimary",
                        {
                            "key": "buttonState.off.bgColor",
                            "type": "color"
                        }, {
                            "key": "buttonState.off.textColor",
                            "type": "color"
                        }
                    ]
                }
            ]
        }
    ]
}
