descriptor.templateCss:
.tb-rpc-button {
    width: 100%;
    height: 100%;
}

.tb-rpc-button .title-container {
    font-size: 0.8em;
    font-weight: normal;
    white-space: nowrap;
    opacity: 0.5;
    margin: 0;
}

.tb-rpc-button .button-container div{
    min-width: 80%
}

.tb-rpc-button .button-container .mat-button{
    width: 100%;
    margin: 0;
}

.tb-rpc-button .button-container .mat-button{
    padding: 0 0px;
}

.tb-rpc-button .error-container {
    position: absolute;
    top: 2%;
    right: 0;
    left: 0;
    z-index: 4;
    height: 14px;
}

.tb-rpc-button .error-container .button-error {
    color: #ff3315;
    white-space: nowrap;
}

descriptor.controllerScript:
let valueSubscription;

self.onInit = function() {
    self.ctx.ngZone.run(function() {
       init();
       self.ctx.detectChanges(true);
    });
};

function init() {
    let utils       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    //let types       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('types'));
    //let toast       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('toast'));
    let translate   = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('translate'));
    //let $q          = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('$q'));
    let http        = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('http'));
    let attributeService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    let rpcUpdateStatus = {};
    let settings = utils.deepClone(self.ctx.settings) || {}; 
    
    self.ctx.$scope.toastTargetId = 'input-widget' + utils.guid();
    self.ctx.$scope.showTitle = settings.title && settings.title.length ? true : false; ////utils.defaultValue(settings.title, true);
    self.ctx.$scope.title = settings.title;
    self.ctx.$scope.options = settings.options;
    self.ctx.$scope.optionStyle = settings.optionStyle;

    self.ctx.$scope.customCheckedStyle = {};
    self.ctx.$scope.customCheckedStyle['min-width'] = '40px';
    self.ctx.$scope.customCheckedStyle['padding']   = '0 2px';
    if (self.ctx.settings.optionStyle.orientation === 'column') {
        self.ctx.$scope.customCheckedStyle['width']     = '100%';
        self.ctx.$scope.customCheckedStyle['margin']    = 0;
    } else {
        self.ctx.$scope.customCheckedStyle['width']     = 100/self.ctx.settings.options.length + '%';
        self.ctx.$scope.customCheckedStyle['margin']    = 0;
    }
    if (self.ctx.settings.optionStyle.checkedOption.isPrimary === false) {
        self.ctx.$scope.customCheckedStyle['background-color']  = self.ctx.$scope.optionStyle.checkedOption.bgColor;
        self.ctx.$scope.customCheckedStyle['color']             = self.ctx.$scope.optionStyle.checkedOption.textColor;
    }

    self.ctx.$scope.customUncheckedStyle = {};
    self.ctx.$scope.customUncheckedStyle['min-width'] = '40px';
    self.ctx.$scope.customUncheckedStyle['padding']   = '0 2px';
    if (self.ctx.settings.optionStyle.orientation === "column") {
        self.ctx.$scope.customUncheckedStyle['width']     = '100%';
        self.ctx.$scope.customUncheckedStyle['margin']    = 0;
    } else {
        self.ctx.$scope.customUncheckedStyle['width']     = 100/self.ctx.settings.options.length + '%';
        self.ctx.$scope.customUncheckedStyle['margin']    = 0;
    }
    if (self.ctx.settings.optionStyle.uncheckedOption.isPrimary === false) {
        self.ctx.$scope.customUncheckedStyle['background-color']  = self.ctx.$scope.optionStyle.uncheckedOption.bgColor;
        self.ctx.$scope.customUncheckedStyle['color']             = self.ctx.$scope.optionStyle.uncheckedOption.textColor;
    }
    
    
    //console.log("onInit", settings);
    
    let parseValueFunction = (data) => data;        // Retrieve data
    if (settings.parseValueFunction && settings.parseValueFunction.length) {
        try {
            parseValueFunction = new Function('data', settings.parseValueFunction);
        } catch (e) {
            parseValueFunction = (data) => data;
        }
    }
    
    let convertValueFunction = (value) => value;    // Update data
    if (settings.convertValueFunction && settings.convertValueFunction.length) {
        try {
            convertValueFunction = new Function('value', settings.convertValueFunction);
        } catch (e) {
            convertValueFunction = (value) => value;
        }
    }

    // subscribe attribute & timeseries 
    function onDataUpdatedForSubscribe(subscription, apply) {
        if (subscription.data.length) {
            var keyData = subscription.data[0];
            if (keyData && keyData.data && keyData.data[0]) {
                var attrValue = keyData.data[0][1];
                if (attrValue) {
                    try {
                        self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = parseValueFunction(attrValue);  ////angular.fromJson(attrValue)
                    } catch (e){
                        self.ctx.$scope.error = 'parseValueFunction(value) error!';
                    }
                }
            }
        }
        
        if (apply) {
            self.ctx.detectChanges(); //self.ctx.$scope.$digest();
        }
    }
    function onDataUpdateErrorForSubscribe(subscription, e) {
        var exceptionData = utils.parseException(e);
        var errorText = exceptionData.name;
        if (exceptionData.message) {
            errorText += ': ' + exceptionData.message;
        }
        self.ctx.$scope.error = errorText;
    }
    function subscribeAttributesOrTimeseries(type, entityType, entityId, retrieveMethod, key) {
        var valueSubscriptionInfo = [{
            type:       type,
            entityType: entityType,
            entityId:   entityId
        }];
        
        if (retrieveMethod == 'attribute') {
            valueSubscriptionInfo[0].attributes = [
                {name: key}
            ];
        } else {
            valueSubscriptionInfo[0].timeseries = [
                {name: key}
            ];
        }
        
        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo (
            'latest', valueSubscriptionInfo, subscriptionOptions, false, true).subscribe( //types.widgetType.latest.value
            (subscription) => {
                valueSubscription = subscription;
            }
        );    
    }
    
    // update attribute
    function updateAttributes(entityType, entityId, attributeScope, key, value) { 
        let attributes = [];
        attributes.push({
            "key": key,                             // attribute name,
            "value": convertValueFunction(value)    // attribute value
        });

        ////attributeService.saveEntityAttributes(self.ctx.datasources[0].entityType, self.ctx.datasources[0].entityId,
        attributeService.saveEntityAttributes({"entityType":entityType, "id":entityId}, 
            attributeScope, attributes).subscribe(
            function success() {
                retrieveValue();  //self.ctx.$scope.originalValue = self.ctx.$scope.currentValue = value;
                self.ctx.$scope.error = "";
            },
            function fail(rejection) {
                if (settings.showError) {
                    self.ctx.$scope.error =
                        rejection.status + ": " +
                        rejection.statusText;
                }
            }
        );
    }
    
    // update timeseries 
    function saveEntityTimeseries(entityType, entityId, telemetries) {
        var telemetriesData = {};
        for (var a = 0; a < telemetries.length; a++) {
            if (typeof telemetries[a].value !== 'undefined' && telemetries[a].value !== null) {
                telemetriesData[telemetries[a].key] = telemetries[a].value;
            }
        }
        if (Object.keys(telemetriesData).length) {
            var url = '/api/plugins/telemetry/' + entityType + '/' + entityId + '/timeseries/scope';
            return http.post(url, telemetriesData);
        }
        return null;
    }
    function updateTimeseries(entityType, entityId, key, value) {
        //var datasource = self.ctx.datasources[0];
        let observable = saveEntityTimeseries(
            entityType,     // datasource.entityType,
            entityId,       // datasource.entityId,
            [
                {
                    key: key,
                    value: convertValueFunction(value)
                }
            ]
        );
        if (observable) {
            observable.subscribe(
                function success() {
                    /*if (settings.showResultMessage) {
                        $scope.showSuccessToast(translate.instant('widgets.input-widgets.update-successful'), 1000, 'bottom', 'left', $scope.toastTargetId);
                    }*/
                    retrieveValue();    //$scope.originalValue = $scope.currentValue = value;
                },
                function fail() {
                    /*if (settings.showResultMessage) {
                        $scope.showErrorToast(translate.instant('widgets.input-widgets.update-failed'), 'bottom', 'left', $scope.toastTargetId);
                    }*/
                    self.ctx.$scope.error = translate.instant('widgets.input-widgets.update-failed');
                }
            );
        }
    }

    // RPC retrieve value
    function rpcRetrieveValue(method, timeout) {
        if (method && method.length) {
            self.ctx.$scope.error = '';
            self.ctx.controlApi.sendTwoWayCommand(method, null, timeout).subscribe(
                function success(responseBody) {
                    ////self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = responseBody;
                    self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = parseValueFunction(responseBody);

                    ////self.ctx.$scope.$digest(); // Error: $digest already in progress
                },
                function fail() {
                    self.ctx.$scope.error = self.ctx.defaultSubscription.rpcErrorText;
                }
            );
        } else {
            self.ctx.$scope.error = "retrieveRPCMethod is null.";
        }
    }
    // RPC update value
    function rpcUpdateValue(method, value, timeout) {
        if (rpcUpdateStatus.executingUpdateValue) {
            rpcUpdateStatus.scheduledValue = value;
            return;
        } else {
            rpcUpdateStatus.scheduledValue = null;
            rpcUpdateStatus.rpcValue = value;
            rpcUpdateStatus.executingUpdateValue = true;
        }
        self.ctx.$scope.error = '';
        self.ctx.controlApi.sendOneWayCommand(method, convertValueFunction(value), timeout).subscribe(
            () => {
                rpcUpdateStatus.executingUpdateValue = false;
                if (rpcUpdateStatus.scheduledValue !== null && rpcUpdateStatus.scheduledValue != rpcUpdateStatus.rpcValue) {
                    rpcUpdateValue(rpcUpdateStatus.scheduledValue);
                } else {
                    retrieveValue(); // self.ctx.$scope.originalValue = self.ctx.$scope.currentValue = value;
                }
            },
            () => {
                rpcUpdateStatus.executingUpdateValue = false;
                self.ctx.$scope.error = self.ctx.defaultSubscription.rpcErrorText;
            }
        );
    }

    // update value
    self.ctx.$scope.updateValue = function(option) {
        //console.log("updateValue()", settings);
        
        if (settings.updateMethod === "SHARED_SCOPE" || settings.updateMethod === "SERVER_SCOPE") {
            updateAttributes("DEVICE", //types.entityType.device, 
                self.ctx.defaultSubscription.targetDeviceId, 
                settings.updateMethod, 
                settings.updateAttributeKey, 
                option.attributeValue);
                
        } else if (settings.updateMethod === "timeseries") {
            updateTimeseries("DEVICE", // types.entityType.device,  types.constant.js
                self.ctx.defaultSubscription.targetDeviceId, 
                settings.updateAttributeKey, 
                option.attributeValue);
                
        } else if (settings.updateMethod === "rpc") {
            rpcUpdateValue(settings.updateRPCMethod, 
                option.attributeValue, 
                settings.requestTimeout);
            
        } else {
            self.ctx.$scope.error = "updateMethod is error!";

        }
    }

    // retrieve value
    var firstRetrieveValue = true;
    function retrieveValue() {
        let rpcEnabled = self.ctx.defaultSubscription.rpcEnabled;
        let isSimulated = self.ctx.$scope.widgetEditMode;
        if (!rpcEnabled) {
            self.ctx.$scope.error = 'Target device is not set!';
        } else {
            if (!isSimulated) {
                if (settings.retrieveMethod == 'rpc') {
                    rpcRetrieveValue(settings.retrieveRPCMethod, settings.requestTimeout);
                } else if (settings.retrieveMethod == 'attribute' || settings.retrieveMethod == 'timeseries') {
                    if (firstRetrieveValue) {
                        subscribeAttributesOrTimeseries("entity", // types.datasourceType.entity, in types.constant.js
                            "DEVICE", //types.entityType.device, in types.constant.js
                            self.ctx.defaultSubscription.targetDeviceId, 
                            settings.retrieveMethod, 
                            settings.retrieveAttributeKey);
                    }
                }
            } 
        }
        
        firstRetrieveValue = false;
    }
    
    retrieveValue();
};

self.onResize = function() {
};

self.onDestory = function() {
    if (valueSubscription) {
        self.ctx.subscriptionApi.removeSubscription(valueSubscription.id);
    }
};

