descriptor.templateCss:
.tb-rpc-button {
    width: 100%;
    height: 100%;
}

.tb-rpc-button .title-container {
    font-size: 0.8em;
    font-weight: normal;
    white-space: nowrap;
    opacity: 0.5;
    margin: 0;
}

.tb-rpc-button .button-container div{
    min-width: 80%
}

.tb-rpc-button .button-container .mat-button{
    width: 100%;
    margin: 0;
}

.tb-rpc-button .button-container .mat-button{
    padding: 0 0px;
}

/*TC 20230529 for mat-mdc-raised-button*/
.tb-rpc-button .button-container .mat-mdc-raised-button {
    width: 100%;
}



.entity-title {
    font-weight: bold;
    font-size: 22px;
    padding-top: 12px;
    padding-bottom: 6px;
    color: #666;
}


.tb-rpc-button .show-label label {
    display: block;
}

label {
    display: none;
}

md-toast{
    min-width: 0;
}

.tb-toast {
    font-size: 14px!important;
}

descriptor.controllerScript:
// v.1.0.0, TC 2023-05-29

// placeholder="{{ 'widgets.input-widgets.time' | translate }}"

let valueSubscription;
let $scope;

self.onInit = function() {
    //console.log("self.onInit()");
    self.ctx.ngZone.run(function() {
       init();
       self.ctx.detectChanges(true);
    });
};


function init() {

    $scope = self.ctx.$scope;
    let utils       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    //let types       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('types'));
    //let toast       = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('toast'));
    let translate   = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('translate'));
    //let $q          = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('$q'));
    let http        = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('http'));
    let attributeService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    let rpcUpdateStatus = {};
    let settings = utils.deepClone(self.ctx.settings) || {};

    self.ctx.$scope.toastTargetId = 'input-widget' + utils.guid();
    $scope.settings = settings;
    $scope.isValidParameter = true;                 // yes,html
    $scope.entityDetected = true;       // false;   // yes,html
    $scope.dataKeyDetected = true;      // false;   // yes,html
    $scope.message = translate.instant('widgets.input-widgets.no-entity-selected');    // yes,html
    $scope.requiredErrorMessage = utils.customTranslation(settings.requiredErrorMessage, settings.requiredErrorMessage) || translate.instant ('widgets.input-widgets.entity-attribute-required');    // yes,html
    if (settings.widgetTitle && settings.widgetTitle.length) {
        $scope.titleTemplate = utils.customTranslation(settings.widgetTitle, settings.widgetTitle);
    } else {
        $scope.titleTemplate = self.ctx.widgetConfig.title;
    }
    //self.ctx.widgetTitle = utils.createLabelFromDatasource(self.ctx.datasources[0], $scope.titleTemplate);
    self.ctx.widgetTitle = $scope.titleTemplate;
    self.ctx.$scope.showTitle = self.ctx.settings.title && self.ctx.settings.title.length ? true : false;
    self.ctx.$scope.title = self.ctx.settings.title;
    self.ctx.$scope.buttonState = settings.buttonState;
    if (settings.buttonState.on.isPrimary === false) {
            self.ctx.$scope.customOnStyle = {
                'background-color': self.ctx.$scope.buttonState.on.bgColor,
                'color': self.ctx.$scope.buttonState.on.textColor,
            };
    }
    if (settings.buttonState.off.isPrimary === false) {
            self.ctx.$scope.customOffStyle = {
                'background-color': self.ctx.$scope.buttonState.off.bgColor,
                'color': self.ctx.$scope.buttonState.off.textColor,
            };
    }

    //console.log("onInit", settings);

    let stateParams = {};
    if (!self.ctx.isEdit && self.ctx.stateController) {
        stateParams = self.ctx.stateController.getStateParams();
    }
    let patternCommonParam = settings.patternCommonParam;

    function parseValue(data) {
        var parsed = data;
        if (typeof settings.buttonState.on.value === "string") {
            if (typeof data === "boolean") {
                //parsed = data===true ? "true" : "false";
                parsed = data===true ? settings.buttonState.on.value : settings.buttonState.off.value;
                
            } else if (typeof data === "number") {
                //parsed = data===1 ? "true" : "false";
                parsed = data!==0 ? settings.buttonState.on.value : settings.buttonState.off.value;
                
            }
        } else if (typeof settings.buttonState.on.value === "boolean") {
            if (typeof data === "string") {
                parsed = data==="true" ? true : false;
            } else if (typeof data === "number") {
                parsed = data!==0 ? true : false;
            }
        }
        return parsed;
    }

    let patternParamOfetrievedAttributeKeyOrMethodName = " ";
    if (settings.retrieveMethod === "attribute" || settings.retrieveMethod === "SHARED_SCOPE" || settings.retrieveMethod === "SERVER_SCOPE") {
        patternParamOfetrievedAttributeKeyOrMethodName = settings.patternParamOfRetrieveAttributeKey;
    } else if (settings.retrieveMethod === "timeseries") {
        patternParamOfetrievedAttributeKeyOrMethodName = settings.patternParamOfRetrieveTimeseriesKey;
    } else if (settings.retrieveMethod === "rpc") {
        patternParamOfetrievedAttributeKeyOrMethodName = settings.patternParamOfRetrieveRPCMethodName;
    }

    let convertRetrievedAttributeKeyOrMethodNameFunction = (stateParams, patternParamOfetrievedAttributeKeyOrMethodName, patternCommonParam) => patternParamOfetrievedAttributeKeyOrMethodName + patternCommonParam;
    if (settings.convertRetrievedAttributeKeyOrMethodNameFunction && settings.convertRetrievedAttributeKeyOrMethodNameFunction.length) {
        try {
            convertRetrievedAttributeKeyOrMethodNameFunction = new Function('stateParams', 'patternParamOfetrievedAttributeKeyOrMethodName', 'patternCommonParam', settings.convertRetrievedAttributeKeyOrMethodNameFunction);
        } catch (e) {
            convertRetrievedAttributeKeyOrMethodNameFunction = (stateParams, patternParamOfetrievedAttributeKeyOrMethodName, patternCommonParam) => patternParamOfetrievedAttributeKeyOrMethodName + patternCommonParam;
        }
    }
    let retrievedAttributeKeyOrMethodName = convertRetrievedAttributeKeyOrMethodNameFunction(stateParams, patternParamOfetrievedAttributeKeyOrMethodName, patternCommonParam);

    let convertRetrievedValueFunction = parseValue; //(value) => value;  // Retrieve data
    if (settings.convertRetrievedValueFunction && settings.convertRetrievedValueFunction.length) {
        try {
            convertRetrievedValueFunction = new Function('value', settings.convertRetrievedValueFunction);
        } catch (e) {
            convertRetrievedValueFunction = parseValue; //(value) => value；
        }
    }

    /*function prefixInteger(num, n) {
        return (Array(n).join(0) + num).slice(-n);
    }    
    function convertValue(value) {
        // Using update data: convert 'Thu Jan 01 1970 09:30:00 GMT+0800 (香港標準時間)' to "09:30"
        let result;
        var m = moment(value);
        if (m.isValid()) {
            //result = prefixInteger(m.hour(), 2) + ":" +　prefixInteger(m.minute(), 2);
            result = (Array(2).join(0) + m.hour()).slice(-2) + ":" +　(Array(2).join(0) + m.minute()).slice(-2);
        }
        return result;
    }*/
    
    let patternParamOfUpdateAttributeKeyOrMethodName = " ";
    if (settings.updateMethod === "SHARED_SCOPE" || settings.updateMethod === "SERVER_SCOPE") {
        patternParamOfUpdateAttributeKeyOrMethodName = settings.patternParamOfUpdateAttributeKey;
    } else if (settings.updateMethod === "timeseries") {
        patternParamOfUpdateAttributeKeyOrMethodName = settings.patternParamOfUpdateTimeseriesKey;
    } else if (settings.updateMethod === "rpc") {
        patternParamOfUpdateAttributeKeyOrMethodName = settings.patternParamOfUpdateRPCMethodName;
    }

    let convertUpdatedAttributeKeyOrMethodNameFunction = (stateParams, patternParamOfUpdateAttributeKeyOrMethodName, patternCommonParam) => patternParamOfUpdateAttributeKeyOrMethodName + patternCommonParam;
    if (settings.convertUpdatedAttributeKeyOrMethodNameFunction && settings.convertUpdatedAttributeKeyOrMethodNameFunction.length) {
        try {
            convertUpdatedAttributeKeyOrMethodNameFunction = new Function('stateParams', 'patternParamOfUpdateAttributeKeyOrMethodName', 'patternCommonParam', settings.convertUpdatedAttributeKeyOrMethodNameFunction);
        } catch (e) {
            convertUpdatedAttributeKeyOrMethodNameFunction = (stateParams, patternParamOfUpdateAttributeKeyOrMethodName, patternCommonParam) => patternParamOfUpdateAttributeKeyOrMethodName + patternCommonParam;
        }
    }
    let updateAttributeKeyOrMethodName = convertUpdatedAttributeKeyOrMethodNameFunction(stateParams, patternParamOfUpdateAttributeKeyOrMethodName, patternCommonParam);

    let convertUpdatedValueFunction = (value) => value; //convertValue;    // Update data
    if (settings.convertUpdatedValueFunction && settings.convertUpdatedValueFunction.length) {
        try {
            convertUpdatedValueFunction = new Function('value', settings.convertUpdatedValueFunction);
        } catch (e) {
            convertUpdatedValueFunction = (value) => value; //convertValue;
        }
    }

    // subscribe attribute & timeseries
    function onDataUpdatedForSubscribe(subscription, apply) {
        if (subscription.data.length) {
            var keyData = subscription.data[0];
            if (keyData && keyData.data && keyData.data[0]) {
                var attrValue = keyData.data[0][1];
                ////if (attrValue) {
                    try {
                        self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = convertRetrievedValueFunction(attrValue);  ////angular.fromJson(attrValue)
                    } catch (e){
                        //self.ctx.$scope.error = 'convertRetrievedValueFunction(value) error!'
                        $scope.showErrorToast(translate.instant(e), 'bottom', 'left', $scope.toastTargetId);
                    }
                ////}
            }
        }
        
        if (apply) {
            self.ctx.detectChanges(); //self.ctx.$scope.$digest();
        }
    }
    function onDataUpdateErrorForSubscribe(subscription, e) {
        var exceptionData = utils.parseException(e);
        var errorText = exceptionData.name;
        if (exceptionData.message) {
            errorText += ': ' + exceptionData.message;
        }
        self.ctx.$scope.error = errorText;
    }

    /*function subscribeAttributesOrTimeseries(type, entityType, entityId, retrieveMethod, key) {
        var valueSubscriptionInfo = [{
            type:       type,
            entityType: entityType,
            entityId:   entityId
        }];
        
        if (retrieveMethod == 'attribute') {
            valueSubscriptionInfo[0].attributes = [
                {name: key}
            ];
        } else {
            valueSubscriptionInfo[0].timeseries = [
                {name: key}
            ];
        }
        
        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo (
            'latest', valueSubscriptionInfo, subscriptionOptions, false, true).subscribe( //types.widgetType.latest.value
            (subscription) => {
                valueSubscription = subscription;
            }
        );    
    }*/

    function subscribeAttributes(type, entityType, entityId, key) { //retrieveMethod,
        var valueSubscriptionInfo = [{
            type:       type,
            entityType: entityType,
            entityId:   entityId
        }];

        //if (retrieveMethod == 'attribute') {
            valueSubscriptionInfo[0].attributes = [
                {name: key}
            ];
        //} else {
        //    valueSubscriptionInfo[0].timeseries = [
        //        {name: key}
        //    ];
        //}
        
        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo (
            'latest', valueSubscriptionInfo, subscriptionOptions, false, true).subscribe( //types.widgetType.latest.value
            (subscription) => {
                valueSubscription = subscription;
            }
        );    
    }
    function subscribeTimeseries(type, entityType, entityId, key) { //retrieveMethod,
        var valueSubscriptionInfo = [{
            type:       type,
            entityType: entityType,
            entityId:   entityId
        }];
        
        //if (retrieveMethod == 'attribute') {
        //    valueSubscriptionInfo[0].attributes = [
        //        {name: key}
        //    ];
        //} else {
            valueSubscriptionInfo[0].timeseries = [
                {name: key}
            ];
        //}
        
        var subscriptionOptions = {
            callbacks: {
                onDataUpdated: onDataUpdatedForSubscribe,
                onDataUpdateError: onDataUpdateErrorForSubscribe
            }
        };
        self.ctx.subscriptionApi.createSubscriptionFromInfo (
            'latest', valueSubscriptionInfo, subscriptionOptions, false, true).subscribe( //types.widgetType.latest.value
            (subscription) => {
                valueSubscription = subscription;
            }
        );    
    }
    
    // update attribute
    function updateAttributes(entityType, entityId, attributeScope, key, value) {
        let attributes = [];
        attributes.push({
            "key": key,         // attribute name,
            "value": value      // attribute value
        });

        ////attributeService.saveEntityAttributes(self.ctx.datasources[0].entityType, self.ctx.datasources[0].entityId,
        attributeService.saveEntityAttributes({"entityType":entityType, "id":entityId}, 
            attributeScope, attributes).subscribe(
            function success() {
                ////self.ctx.$scope.error = "";
                if (settings.showResultMessage) {
                        $scope.showSuccessToast(translate.instant('widgets.input-widgets.update-successful'), 1000, 'bottom', 'left', $scope.toastTargetId);
                }
                retrieveValue();  //self.ctx.$scope.originalValue = self.ctx.$scope.currentValue = value;
            },
            function fail(rejection) {
                ////if (self.ctx.settings.showError) {
                ////    self.ctx.$scope.error = rejection.status + ": " + rejection.statusText;
                ////}
                if (settings.showResultMessage) {
                        $scope.showErrorToast(translate.instant('widgets.input-widgets.update-failed'), 'bottom', 'left', $scope.toastTargetId);
                }
            }
        );
    }
    
    // update timeseries 
    function saveEntityTimeseries(entityType, entityId, telemetries) {
        var telemetriesData = {};
        for (var a = 0; a < telemetries.length; a++) {
            if (typeof telemetries[a].value !== 'undefined' && telemetries[a].value !== null) {
                telemetriesData[telemetries[a].key] = telemetries[a].value;
            }
        }
        if (Object.keys(telemetriesData).length) {
            var url = '/api/plugins/telemetry/' + entityType + '/' + entityId + '/timeseries/scope';
            return http.post(url, telemetriesData);
        }
        return null;
    }
    function updateTimeseries(entityType, entityId, key, value) {
        //var datasource = self.ctx.datasources[0];
        let observable = saveEntityTimeseries(
            entityType,     // datasource.entityType,
            entityId,       // datasource.entityId,
            [
                {
                    key: key,
                    value: value
                }
            ]
        );
        if (observable) {
            observable.subscribe(
                function success() {
                    ////self.ctx.$scope.error = "";
                    if (settings.showResultMessage) {
                        $scope.showSuccessToast(translate.instant('widgets.input-widgets.update-successful'), 1000, 'bottom', 'left', $scope.toastTargetId);
                    }
                    retrieveValue();    //self.ctx.$scope.originalValue = self.ctx.$scope.currentValue = value;
                },
                function fail() {
                    ////if (self.ctx.settings.showError)
                    ////    self.ctx.$scope.error = $translate.instant('widgets.input-widgets.update-failed');
                    ////}
                    if (settings.showResultMessage) {
                        $scope.showErrorToast(translate.instant('widgets.input-widgets.update-failed'), 'bottom', 'left', $scope.toastTargetId);
                    }
                }
            );
        }
    }

    // RPC retrieve value
    function rpcRetrieveValue(method, timeout) {
        if (method && method.length) {
            self.ctx.$scope.error = '';
            self.ctx.controlApi.sendTwoWayCommand(method, null, timeout).subscribe(
                function success(responseBody) {
                    try {
                        self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = convertRetrievedValueFunction(responseBody);
                        ////self.ctx.$scope.$digest(); // Error: $digest already in progress
                    } catch (e){
                        //self.ctx.$scope.error = 'convertRetrievedValueFunction(value) error!'
                        $scope.showErrorToast(translate.instant(e), 'bottom', 'left', $scope.toastTargetId);
                    }
                },
                function fail() {
                    ////self.ctx.$scope.error = self.ctx.defaultSubscription.rpcErrorText;
		    $scope.showErrorToast(translate.instant(self.ctx.defaultSubscription.rpcErrorText), 'bottom', 'left', $scope.toastTargetId);
                }
            );
        } else {
            ////self.ctx.$scope.error = "retrievedAttributeKeyOrMethodName is null.";
            $scope.showErrorToast(translate.instant('retrievedAttributeKeyOrMethodName is null.'), 'bottom', 'left', $scope.toastTargetId);
        }
    }
    // RPC update value
    function rpcUpdateValue(method, value, timeout) {
        if (rpcUpdateStatus.executingUpdateValue) {
            rpcUpdateStatus.scheduledValue = value;
            return;
        } else {
            rpcUpdateStatus.scheduledValue = null;
            rpcUpdateStatus.rpcValue = value;
            rpcUpdateStatus.executingUpdateValue = true;
        }
        self.ctx.$scope.error = '';
        self.ctx.controlApi.sendOneWayCommand(method, value, timeout).subscribe(
            () => {
                rpcUpdateStatus.executingUpdateValue = false;
                if (rpcUpdateStatus.scheduledValue !== null && rpcUpdateStatus.scheduledValue != rpcUpdateStatus.rpcValue) {
                    rpcUpdateValue(rpcUpdateStatus.scheduledValue);
                } else {
                    ////self.ctx.$scope.error = "";
                    if (settings.showResultMessage) {
			$scope.showSuccessToast(translate.instant('widgets.input-widgets.update-successful'), 1000, 'bottom', 'left', $scope.toastTargetId);
                    }
                    retrieveValue(); // self.ctx.$scope.originalValue = self.ctx.$scope.currentValue = value;
                }
            },
            () => {
                rpcUpdateStatus.executingUpdateValue = false;
                ////if (self.ctx.settings.showError) {
                ////    self.ctx.$scope.error = self.ctx.defaultSubscription.rpcErrorText;
                ////}
                if (settings.showResultMessage) {
		    $scope.showErrorToast(translate.instant('widgets.input-widgets.update-failed'), 'bottom', 'left', $scope.toastTargetId);
                }
            }
        );
    }

    // update value

    self.ctx.$scope.updateValue = function(on_off) {
        //console.log("updateValue()", settings);

        let value = on_off ? settings.buttonState.off.value : 
            settings.buttonState.on.value;
        let newValue = convertUpdatedValueFunction(value);
        //console.log("updateValue():", newValue);
        
        if (newValue){
            if (settings.updateMethod === "SHARED_SCOPE" || settings.updateMethod === "SERVER_SCOPE") {
                updateAttributes("DEVICE", //types.entityType.device, 
                    self.ctx.defaultSubscription.targetDeviceId, 
                    settings.updateMethod, 
                    updateAttributeKeyOrMethodName, //settings.patternParamOfUpdateAttributeKey, 
                    newValue);
                    
            } else if (settings.updateMethod === "timeseries") {
                updateTimeseries("DEVICE", // types.entityType.device,  types.constant.js
                    self.ctx.defaultSubscription.targetDeviceId, 
                    updateAttributeKeyOrMethodName, //settings.patternParamOfUpdateTimeseriesKey, 
                    newValue);
                    
            } else if (settings.updateMethod === "rpc") {
                rpcUpdateValue(updateAttributeKeyOrMethodName, //settings.patternParamOfUpdateRPCMethodName, 
                    newValue, 
                    settings.requestTimeout);
                
            } else {
                self.ctx.$scope.error = "updateMethod is error!";
    
            }
        }
    }

    // retrieve value
    var firstRetrieveValue = true;
    function retrieveValue() {
        let rpcEnabled = self.ctx.defaultSubscription.rpcEnabled;
        let isSimulated = self.ctx.$scope.widgetEditMode;
        if (!rpcEnabled) {
            self.ctx.$scope.error = 'Target device is not set!';
        } else {
            if (!isSimulated) {
                if (settings.retrieveMethod == 'rpc') {
                    rpcRetrieveValue(retrievedAttributeKeyOrMethodName, settings.requestTimeout); //settings.patternParamOfRetrieveRPCMethodName
                } else if (settings.retrieveMethod == 'attribute') {
                    if (firstRetrieveValue) {
                        subscribeAttributes("entity", // types.datasourceType.entity, in types.constant.js
                            "DEVICE", //types.entityType.device, in types.constant.js
                            self.ctx.defaultSubscription.targetDeviceId, 
                            retrievedAttributeKeyOrMethodName); //settings.patternParamOfRetrieveAttributeKey
                    }
                } else if (settings.retrieveMethod == 'timeseries') {
                    if (firstRetrieveValue) {
                        subscribeTimeseries("entity", // types.datasourceType.entity, in types.constant.js
                            "DEVICE", //types.entityType.device, in types.constant.js
                            self.ctx.defaultSubscription.targetDeviceId, 
                            retrievedAttributeKeyOrMethodName); //settings.patternParamOfRetrieveTimeseriesKey
                    }
                }
            } 
        }
        
        firstRetrieveValue = false;
    }
    
    retrieveValue();
};

self.onResize = function() {
    $scope.smallWidthContainer = (self.ctx.$container[0].offsetWidth < 320) ? true : false;
    $scope.changeAlignment = false;
};

self.onDestory = function() {
    if (valueSubscription) {
        self.ctx.subscriptionApi.removeSubscription(valueSubscription.id);
    }
};

/*self.onDataUpdated = function() {
    try{
        self.ctx.$scope.currentValue = self.ctx.$scope.originalValue = self.ctx.data[0].data[0][1];
        self.ctx.$scope.$digest();
    } catch (e) {
        console.log(e);
    }
}*/

