descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "title": {
                "title": "Widget title",
                "type": "string",
                "default": ""
            },

            "retrieveAttributes": {
                "type": "object",
                "title": "Retrieve device attributes",
                "properties": {
                    "adjusted": {
                        "title": "Adjusted device attribute",
                        "type": "string",
                        "default": ""
                    },
                    "min": {
                        "title": "Min value of device attribute",
                        "type": "string",
                        "default": ""
                    },
                    "max": {
                        "title": "Max value of device attribute",
                        "type": "string",
                        "default": ""
                    },
                    "step": {
                        "title": "Step value of device attribute",
                        "type": "string",
                        "default": ""
                    },
                    "unit": {
                        "title": "Unit of device attribute",
                        "type": "string",
                        "default": ""
                    }
                },
                "required": [
                    "adjusted",
                    "min",
                    "max",
                    "step",
                    "unit"
                ]
            },

            "updateMethod": {
                "title": "Update double value using method",
                "type": "string",
                "default": "rpc"
            },
            "updateAttributeKey": {
                "title": "Key of attribute being updated",
                "type": "string",
                "default": ""
            },
            "updateTimeseriesKey": {
                "title": "Key of timeseries value being updated(?)",
                "type": "string",
                "default": ""
            },
            "updateRPCMethod": {
                "title": "Update value using client-side RPC method",
                "type": "string",
                "default": ""
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 5000
            },
            "convertValueFunction": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value; /* console.log(value); */"
            }
        },

        "required": [
            "retrieveAttributes",
            "updateMethod",
            "convertValueFunction"
        ]
    },
    "form": [
        "title",

        "retrieveAttributes",
        {
            "type": "help",
            "description": "<I><small>Note: Retrieve the above attributes from device.</small></I>"
        },

        {
            "key": "updateMethod",
            "type": "rc-select",
            "multiple": false,
            "items": [{
                    "value": "SHARED_SCOPE",
                    "label": "Update shared attribute"
                },
                {
                    "value": "SERVER_SCOPE",
                    "label": "Update server attribute"
                },
                {
                    "value": "timeseries",
                    "label": "Update timeseries"
                },
                {
                    "value": "rpc",
                    "label": "Call RPC set value method"
                }
            ]
        },
        {
            "key": "updateAttributeKey",
            "condition": "model.updateMethod === 'SHARED_SCOPE' || model.updateMethod === 'SERVER_SCOPE'"
        },
        {
            "key": "updateTimeseriesKey",
            "condition": "model.updateMethod === 'timeseries'"
        },
        {
            "key": "updateRPCMethod",
            "condition": "model.updateMethod === 'rpc'"
        },
        {
            "key": "requestTimeout",
            "condition": "model.updateMethod === 'rpc'"
        },
        {
            "key": "convertValueFunction",
            "type": "javascript"
        },

        {
            "type": "help",
            "description": "<I><small>Note: Call the above method and prameters to update value to device.</small></I>"
        }
    ]
}
