descriptor.controllerScript:
// ─────────── JavaScript ───────────
self.onInit = function() {
  // Clear everything, inject only our dark card
  self.ctx.$container
    .empty()
    .append(`
      <div class="re-card">
        <div class="re-title">Real Energy Into Load</div>
        <div class="re-value" id="reValue">–</div>
      </div>
    `);

  // Force parent widget element transparent (in case host styling differs)
  this.ctx.$container.closest('.tb-widget').css({
    'background': 'transparent',
    'box-shadow': 'none'
  });

  self.onResize();
};

self.onDataUpdated = function() {
  const d = self.ctx.data[0];
  if (d && d.data && d.data.length) {
    const raw = d.data[d.data.length - 1][1];
    let txt = raw;
    if (!isNaN(parseFloat(raw))) {
      const dec  = (d.dataKey.decimals===0||d.dataKey.decimals)?d.dataKey.decimals:self.ctx.decimals;
      const unit = d.dataKey.units || self.ctx.units;
      txt = this.ctx.utils.formatValue(raw, dec, unit, true);
    }
    $('#reValue').html(txt);
  }
};

self.onResize = function() {
  const H = this.ctx.height, W = this.ctx.width;
  const titleSz = Math.min(H/10, W/15, 18);
  const valueSz = Math.min(H/2.5, W/4, 36);
  $('.re-title').css('font-size', titleSz + 'px');
  $('.re-value').css('font-size', valueSz + 'px');
};

self.onDestroy = function() {
  // Nothing to clean up
};
