descriptor.templateCss:
.ai-widget-container {
    position: relative;
    width: 100%;
    height: 100%;
    padding: 20px;
    background: #ffffff;
    overflow: auto;
}

.ai-corner-button {
    position: absolute;
    top: 10px;
    right: 10px;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border: none;
    color: white;
    cursor: pointer;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
    transition: all 0.3s ease;
    z-index: 100;
    display: flex;
    align-items: center;
    justify-content: center;
}

.ai-corner-button:hover {
    transform: scale(1.1);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.6);
}

.ai-corner-button:active {
    transform: scale(0.95);
}

.ai-corner-button:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: scale(1);
}

.loading-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 0.95);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    z-index: 99;
}

.spinner {
    border: 4px solid #f3f3f3;
    border-top: 4px solid #667eea;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.loading-overlay p {
    margin-top: 20px;
    color: #667eea;
    font-size: 16px;
    font-weight: 500;
}

.content-display {
    width: 100%;
    height: calc(100% - 40px);
    margin-top: 60px;
    padding: 20px;
    background: #f8f9fa;
    border-radius: 8px;
    border: 1px solid #e0e0e0;
    overflow-y: auto;
}

.placeholder {
    color: #999;
    text-align: center;
    padding: 40px 20px;
    font-size: 14px;
}

.content-display h1, .content-display h2, .content-display h3 {
    color: #333;
    margin-top: 20px;
    margin-bottom: 10px;
}

.content-display h1 { font-size: 24px; }
.content-display h2 { font-size: 20px; }
.content-display h3 { font-size: 16px; }

.content-display p {
    color: #555;
    line-height: 1.6;
    margin-bottom: 10px;
}

.content-display ul, .content-display ol {
    margin-left: 20px;
    margin-bottom: 10px;
}

.content-display li {
    color: #555;
    line-height: 1.6;
    margin-bottom: 5px;
}

.content-display pre {
    background: #2d2d2d;
    color: #f8f8f2;
    padding: 15px;
    border-radius: 4px;
    overflow-x: auto;
    margin: 10px 0;
}

.content-display code {
    background: #f4f4f4;
    padding: 2px 6px;
    border-radius: 3px;
    font-family: 'Courier New', monospace;
}

.content-display blockquote {
    border-left: 4px solid #667eea;
    padding-left: 15px;
    margin: 15px 0;
    color: #666;
    font-style: italic;
}

.error-message {
    background: #fff3cd;
    border: 1px solid #ffc107;
    color: #856404;
    padding: 15px;
    border-radius: 4px;
    margin: 10px 0;
}

.success-message {
    background: #d4edda;
    border: 1px solid #28a745;
    color: #155724;
    padding: 15px;
    border-radius: 4px;
    margin: 10px 0;
}

descriptor.controllerScript:
// === AI Analysis Widget (tolerant JSON/text response) ===
// Changes from previous version:
// - Fetch reads response as text first, then safely tries JSON.
// - Cleans common Markdown fences (```json ... ```).
// - Adds Accept header to be explicit about accepted formats.
// - Keeps everything else (saving attribute, pretty display) intact.

let currentTelemetryData = {};

self.onInit = function() {
    const analyzeBtn = document.getElementById('ai-analyze-btn');
    if (analyzeBtn) {
        analyzeBtn.addEventListener('click', runAIAnalysis);
    }
    loadExistingAttribute();
};

self.onDataUpdated = function() {
    const data = self.ctx.data;
    if (!data || data.length === 0) return;

    currentTelemetryData = {};
    data.forEach(function(dataSource) {
        if (dataSource.data && dataSource.data.length > 0) {
            const latestPoint = dataSource.data[dataSource.data.length - 1];
            currentTelemetryData[dataSource.dataKey.name] = latestPoint[1];
        }
    });

    loadExistingAttribute();
};



function loadExistingAttribute() {
    const settings = self.ctx.settings;
    const assetId = settings.assetId;
    const attributeName = settings.attributeName || 'ai_response';
    const attributeScope = settings.attributeScope || 'SERVER_SCOPE';

    if (!assetId) return;

    self.ctx.attributeService.getEntityAttributes(
        { entityType: 'ASSET', id: assetId },
        attributeScope,
        [attributeName]
    ).subscribe(
        function(attributeData) {
            if (attributeData && attributeData.length > 0) {
                const content = attributeData[0].value;
                displayContent(content);
            }
        },
        function(error) {
            console.error('Error loading existing attribute:', error);
        }
    );
}

function fetchDeviceTelemetry(deviceId) {
    return new Promise(function(resolve, reject) {
        self.ctx.http.get('/api/plugins/telemetry/DEVICE/' + deviceId + '/values/timeseries?keys=').subscribe(
            function(telemetryData) {
                const flatData = {};
                for (var key in telemetryData) {
                    if (telemetryData.hasOwnProperty(key) && telemetryData[key].length > 0) {
                        flatData[key] = telemetryData[key][0].value;
                    }
                }
                resolve(flatData);
            },
            function(error) {
                console.error('Error fetching telemetry:', error);
                reject(error);
            }
        );
    });
}

// Helper: parse server text that might be JSON or a ```json fenced block
function parsePossiblyFencedJSON(text) {
    // Fast-path attempt
    try { return { parsed: JSON.parse(text) }; } catch (_) {}

    // Strip common fences like ```json ... ```
    const cleaned = text.trim()
        .replace(/^```(?:json)?\s*/i, '')
        .replace(/```$/, '');

    try { return { parsed: JSON.parse(cleaned) }; } catch (_) {}

    // Last-ditch: try to extract the first {...} block
    const match = cleaned.match(/\{[\s\S]*\}/);
    if (match) {
        try { return { parsed: JSON.parse(match[0]) }; } catch (_) {}
    }
    // Not JSON, return original text
    return { text };
}

function runAIAnalysis() {
    const analyzeBtn = document.getElementById('ai-analyze-btn');
    const loadingIndicator = document.getElementById('loading-indicator');

    const settings = self.ctx.settings;
    const n8nUrl = settings.n8nUrl;
    const deviceId = settings.deviceId;
    const assetId = settings.assetId;
    const attributeName = settings.attributeName || 'ai_response';
    const attributeScope = settings.attributeScope || 'SERVER_SCOPE';
    const requestMethod = settings.requestMethod || 'GET';
    const includePayload = settings.includePayload !== false;

    if (!n8nUrl) {
        displayError('n8n URL is not configured. Please configure the widget settings.');
        return;
    }
    if (!assetId) {
        displayError('Asset ID is not configured. Please configure the widget settings.');
        return;
    }
    if (!deviceId) {
        displayError('Device ID is not configured. Please configure the widget settings.');
        return;
    }

    analyzeBtn.disabled = true;
    loadingIndicator.style.display = 'flex';

    fetchDeviceTelemetry(deviceId)
        .then(function(telemetryData) {
            var urlWithParams = n8nUrl;

            if (requestMethod === 'GET' && includePayload) {
                var params = new URLSearchParams();
                for (var key in telemetryData) {
                    if (telemetryData.hasOwnProperty(key)) {
                        params.append(key, telemetryData[key]);
                    }
                }
                urlWithParams = n8nUrl + '?' + params.toString();
            }

            const requestOptions = {
                method: requestMethod,
                headers: {
                    'Content-Type': 'application/json',
                    // Be explicit about what we can consume
                    'Accept': 'application/json,text/plain;q=0.9,*/*;q=0.8'
                }
            };

            if (requestMethod === 'POST' && includePayload) {
                requestOptions.body = JSON.stringify({
                    telemetry: telemetryData,
                    timestamp: Date.now(),
                    deviceId: deviceId,
                    assetId: assetId
                });
            }

            return fetch(urlWithParams, requestOptions);
        })
        .then(function(response) {
            if (!response.ok) {
                throw new Error('HTTP error! status: ' + response.status);
            }
            // Read as text first; we‚Äôll decide how to handle it.
            return response.text();
        })
        .then(function(text) {
            const { parsed, text: raw } = parsePossiblyFencedJSON(text);
            const result = (parsed !== undefined) ? parsed : raw;

            var aiResponse;
            if (typeof result === 'string') {
                aiResponse = result;
            } else if (result && typeof result === 'object') {
                // Common shapes: { response: "..."} or { data: "..." }
                if (result.response) {
                    aiResponse = result.response;
                } else if (result.data) {
                    aiResponse = result.data;
                } else {
                    aiResponse = JSON.stringify(result, null, 2);
                }
            } else {
                aiResponse = String(result);
            }

            return saveAttributeToAsset(assetId, attributeName, aiResponse, attributeScope)
                .then(function() { return aiResponse; });
        })
        .then(function(aiResponse) {
            displayContent(aiResponse);
            showSuccessMessage('AI analysis completed and saved successfully!');
        })
        .catch(function(error) {
            console.error('Error during AI analysis:', error);
            displayError('Failed to complete AI analysis: ' + error.message);
        })
        .finally(function() {
            analyzeBtn.disabled = false;
            loadingIndicator.style.display = 'none';
        });
}

function saveAttributeToAsset(assetId, attributeName, value, scope) {
    const attributeData = [{
        key: attributeName,
        value: value
    }];

    return new Promise(function(resolve, reject) {
        self.ctx.attributeService.saveEntityAttributes(
            { entityType: 'ASSET', id: assetId },
            scope,
            attributeData
        ).subscribe(
            function() {
                console.log('Attribute saved successfully');
                resolve();
            },
            function(error) {
                console.error('Error saving attribute:', error);
                reject(error);
            }
        );
    });
}

function escapeHtml(str) {
    if (str === null || str === undefined) return '';
    return String(str)
        .replace(/&/g, '&amp;')
        .replace(/</g, '&lt;')
        .replace(/>/g, '&gt;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&#39;');
}

function formatList(items) {
    if (!items || !items.length) {
        return '<p style="margin: 4px 0 12px; font-size: 12px; color: #888;">No data.</p>';
    }
    var html = '<ul style="margin: 4px 0 12px 18px; padding: 0; font-size: 13px;">';
    items.forEach(function(item) {
        html += '<li>' + escapeHtml(item) + '</li>';
    });
    html += '</ul>';
    return html;
}

function formatAnalysisHtml(result) {
    var urgency = (result.urgencyLevel || '').toString().toUpperCase();
    var urgencyColor = '#28a745';
    if (urgency === 'MEDIUM') urgencyColor = '#ffc107';
    else if (urgency === 'HIGH') urgencyColor = '#fd7e14';
    else if (urgency === 'CRITICAL') urgencyColor = '#dc3545';

    var metrics = result.metrics || {};

    function row(label, value) {
        if (!value) return '';
        return '<tr>' +
            '<td style="border: 1px solid #ddd; padding: 4px 8px; font-weight: 600;">' +
                escapeHtml(label) + '</td>' +
            '<td style="border: 1px solid #ddd; padding: 4px 8px;">' +
                escapeHtml(value) + '</td>' +
        '</tr>';
    }

    var metricsHtml =
        '<table style="border-collapse: collapse; margin-top: 4px; margin-bottom: 12px; font-size: 12px;">' +
            row('Total downtime', metrics.totalDowntime) +
            row('Most problematic alarm', metrics.mostProblematicAlarm) +
            row('Estimated production loss', metrics.estimatedProductionLoss) +
        '</table>';

    var html =
        '<div>' +
            '<div style="margin-bottom: 10px; font-size: 13px;">' +
                '<span style="font-weight: 600;">Urgency: </span>' +
                '<span style="font-weight: 700; color: ' + urgencyColor + ';">' +
                    escapeHtml(urgency || 'N/A') +
                '</span>' +
            '</div>' +

            '<h3 style="margin: 4px 0; font-size: 15px;">‚öôÔ∏è Summary</h3>' +
            '<p style="margin: 4px 0 12px; font-size: 13px;">' +
                escapeHtml(result.summary || 'No summary provided.') +
            '</p>' +

            '<h3 style="margin: 4px 0; font-size: 15px;">üö® Critical issues</h3>' +
            formatList(result.criticalIssues) +

            '<h3 style="margin: 4px 0; font-size: 15px;">üìä Key metrics</h3>' +
            metricsHtml +

            '<h3 style="margin: 4px 0; font-size: 15px;">üõ† Recommendations</h3>' +
            formatList(result.recommendations) +

            '<h3 style="margin: 4px 0; font-size: 15px;">üîÆ Predictions</h3>' +
            '<p style="margin: 4px 0 12px; font-size: 13px;">' +
                escapeHtml(result.predictions || 'No predictions provided.') +
            '</p>' +
        '</div>';

    return html;
}


function displayContent(content) {
    const contentDisplay = document.getElementById('ai-content-display');

    var bodyHtml = '';
    var parsed = null;

    // Try to parse JSON (string or object)
    if (typeof content === 'string') {
        try {
            parsed = JSON.parse(content);
        } catch (e) {
            parsed = null;
        }
    } else if (content && typeof content === 'object') {
        parsed = content;
    }

    var looksLikeAnalysis =
        parsed &&
        typeof parsed === 'object' &&
        (parsed.summary || parsed.metrics || parsed.criticalIssues || parsed.recommendations);

    if (looksLikeAnalysis) {
        // Pretty Andon analysis HTML
        bodyHtml = formatAnalysisHtml(parsed);
    } else {
        // Fallback: your old behavior (JSON pretty / plain text)
        var formattedContent;
        try {
            const parsedFallback = JSON.parse(content);
            formattedContent = '<pre>' + JSON.stringify(parsedFallback, null, 2) + '</pre>';
        } catch (e) {
            formattedContent = String(content)
                .replace(/&/g, '&amp;')
                .replace(/</g, '&lt;')
                .replace(/>/g, '&gt;')
                .replace(/\n/g, '<br>')
                .replace(/\t/g, '&nbsp;&nbsp;&nbsp;&nbsp;');
        }
        bodyHtml = '<div>' + formattedContent + '</div>';
    }

    contentDisplay.innerHTML =
        '<div style="color: #28a745; font-size: 12px; margin-bottom: 10px;">' +
            'Last updated: ' + new Date().toLocaleString() +
        '</div>' +
        bodyHtml;
}


function displayError(message) {
    const contentDisplay = document.getElementById('ai-content-display');
    contentDisplay.innerHTML =
        '<div class="error-message">' +
        '<strong>Error:</strong> ' + message +
        '</div>';
}

function showSuccessMessage(message) {
    const contentDisplay = document.getElementById('ai-content-display');
    const successDiv = document.createElement('div');
    successDiv.className = 'success-message';
    successDiv.textContent = message;
    contentDisplay.insertBefore(successDiv, contentDisplay.firstChild);

    setTimeout(function() {
        successDiv.remove();
    }, 3000);
}

